INSERT  INTO `answer`(`id`,`created_date`,`a_order`,`text`,`question_id`) VALUES (224,'2023-06-14 16:53:22',3,'It is a fully managed, Redis- and Memcached-compatible service delivering real-time, cost-optimized performance for modern applications. It  scales to hundreds of millions of operations per second with microsecond response time, and offers enterprise-grade security and reliability.',55);
